const input = $input.all();
let dados = [];

if (input && input.length > 0) {
  if (input.length > 1) {
    dados = input.map(item => item.json || item);
  } else {
    const firstItem = input[0];
    if (firstItem.json && Array.isArray(firstItem.json)) {
      dados = firstItem.json;
    } else {
      dados = input.map(item => item.json || item);
    }
  }
}

function hasNullValues(data) {
  const campos = ['Plano de neg√≥cios', 'TamSamSom', 'Pesquisa qualitativa:', 'Mapeamento concorrentes', 'Persona', 'Pitch', 'Programas'];
  return campos.some(campo => data[campo] === null || data[campo] === undefined);
}

function normalizeResultado(resultado, pontuacao, data) {
  const score = Number(pontuacao || 0);
  if (hasNullValues(data)) {
    return 'N√£o conclu√≠do';
  }
  if (!resultado || resultado === '' || score < 3) {
    return 'Trilha Inicial';
  }
  const resultadoStr = String(resultado).trim();
  if (resultadoStr === 'Aprovado para Incuba√ß√£o') return 'Aprovado Incuba√ß√£o';
  if (resultadoStr === 'Trilha Avan√ßada') return 'Trilha Avan√ßada';
  if (resultadoStr === 'Trilha Inicial') return 'Trilha Inicial';
  return 'Trilha Inicial';
}

function getIndicatorStatus(value) {
  if (value === null || value === undefined) return 'red';
  if (!value || value === '') return 'red';
  const valueStr = String(value).toLowerCase().trim();
  if (valueStr === 'sim') return 'green';
  if (valueStr === 'n√£o') return 'red';
  if (valueStr === 'ainda n√£o realizei') return 'red';
  if (valueStr === 'nao_realizei') return 'red';
  if (valueStr === 'sim, com dados organizados') return 'green';
  if (valueStr === 'dados_organizados') return 'green';
  if (valueStr === 'informal') return 'yellow';
  if (valueStr === 'fiz informalmente, mas sem dados registrados' || valueStr === 'informalmente sem dados registrados') return 'yellow';
  return 'red';
}

const processedData = dados.map((item, index) => {
  const data = item.json || item;
  const pontuacao = Number(data.pontuacao || 0);
  const dataFormatada = data.created_at 
    ? new Date(data.created_at).toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' }) 
    : 'N/A';
  return {
    id: data.id || index + 1,
    nome: String(data.nome || 'N/A').trim(),
    projeto: String(data.projeto || 'N/A').trim(),
    email: String(data.email || 'N/A').trim(),
    whatsapp: String(data.whatsapp || 'N/A'),
    pontuacao: pontuacao,
    created_at: dataFormatada,
    resultado: normalizeResultado(data.resultado, pontuacao, data),
    indicadores: {
      plano_negocios: getIndicatorStatus(data['Plano de neg√≥cios']),
      tam_sam_som: getIndicatorStatus(data['TamSamSom']),
      pesquisa_qualitativa: getIndicatorStatus(data['Pesquisa qualitativa:']),
      mapeamento_concorrentes: getIndicatorStatus(data['Mapeamento concorrentes']),
      persona: getIndicatorStatus(data['Persona']),
      pitch: getIndicatorStatus(data['Pitch']),
      programas: getIndicatorStatus(data['Programas'])
    },
    respostas_originais: {
      plano_negocios: data['Plano de neg√≥cios'] || 'N/A',
      tam_sam_som: data['TamSamSom'] || 'N/A',
      pesquisa_qualitativa: data['Pesquisa qualitativa:'] || 'N/A',
      mapeamento_concorrentes: data['Mapeamento concorrentes'] || 'N/A',
      persona: data['Persona'] || 'N/A',
      pitch: data['Pitch'] || 'N/A',
      programas: data['Programas'] || 'N/A'
    }
  };
});

const hoje = new Date().toLocaleDateString('pt-BR', { timeZone: 'America/Sao_Paulo' });
const novosHoje = processedData.filter(item => {
  if (!item.created_at || item.created_at === 'N/A') return false;
  try {
    const dataItem = new Date(item.created_at);
    const dataStr = dataItem.toLocaleDateString('pt-BR', { timeZone: 'America/Sao_Paulo' });
    return dataStr === hoje;
  } catch (e) {
    return false;
  }
}).length;

const aprovadosIncubacao = processedData.filter(item => item.resultado === 'Aprovado Incuba√ß√£o').length;
const trilhaAvancada = processedData.filter(item => item.resultado === 'Trilha Avan√ßada').length;
const trilhaInicial = processedData.filter(item => item.resultado === 'Trilha Inicial').length;
const naoConcluido = processedData.filter(item => item.resultado === 'N√£o conclu√≠do').length;

// NOVO BLOCO: CONTAR CADASTROS POR M√äS (formato '09/2025') e do m√™s atual
const cadastradosPorMes = {};
processedData.forEach(item => {
  if (item.created_at && item.created_at !== 'N/A') {
    // Data pt-BR ex: '10/09/2025, 08:26:05'
    const partes = item.created_at.split('/');
    if (partes.length >= 2) {
      const mesAno = `${partes[1]}/${partes[2].slice(0,4)}`; // '09/2025'
      cadastradosPorMes[mesAno] = (cadastradosPorMes[mesAno] || 0) + 1;
    }
  }
});
const now = new Date();
const mesAtual = ('0' + (now.getMonth() + 1)).slice(-2) + '/' + now.getFullYear();
const inscricoesMesAtual = cadastradosPorMes[mesAtual] || 0;

// RETORNO FINAL
return {
  json: {
    dados: processedData,
    stats: {
      total: processedData.length,
      novosHoje: novosHoje,
      aprovadosIncubacao: aprovadosIncubacao,
      trilhaAvancada: trilhaAvancada,
      trilhaInicial: trilhaInicial,
      naoConcluido: naoConcluido,
      // Novos campos:
      cadastradosPorMes,
      inscricoesMesAtual,
      mesAtual
    },
    ultimaAtualizacao: new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' })
  }
};

