    ];

    return '<div class="indicators-list">' + 
        items.map(item => 
            `<div><span class="indicator ${indicadores[item.key]}"></span>${item.label}</div>`
        ).join('') + 
        '</div>';
}

// Renderiza tabela com os dados filtrados
function renderTable() {
    const tbody = document.getElementById('tableBody');
    tbody.innerHTML = filteredData.map(user => {
        const statusInfo = getStatus(user.resultado);
        const phoneFormatted = formatPhone(user.whatsapp);
        const digits = String(user.whatsapp || '').replace(/\D/g, '');
        const whatsappLink = digits ? `<a href="https://wa.me/${digits}" target="_blank" class="whatsapp-btn">WhatsApp</a>` : 'N/A';
        const emailLink = user.email && user.email !== 'N/A' ? `<a href="mailto:${user.email}" target="_blank" class="email-btn">Email</a>` : 'N/A';
        const scoreClass = getScoreClass(user.pontuacao);
        const indicatorsHtml = renderIndicators(user.indicadores);

        return '<tr class="hover:bg-gray-50 border-b">' +
            `<td class="p-4"><strong>${user.nome}</strong></td>` +
            `<td class="p-4">${user.projeto}</td>` +
            `<td class="p-4"><span class="badge ${statusInfo.class}">${statusInfo.text}</span></td>` +
            `<td class="p-4"><span class="${scoreClass}">${user.pontuacao}</span></td>` +
            `<td class="p-4">${indicatorsHtml}</td>` +
            `<td class="p-4"><span class="phone">${phoneFormatted}</span><br>${whatsappLink}</td>` +
            `<td class="p-4">${user.email}<br>${emailLink}</td>` +
            `<td class="p-4">${user.created_at}</td>` +
            '</tr>';
    }).join('');
}

// Aplica filtros por status, nome e projeto
function applyFilters() {
    const statusFilter = document.getElementById('filterStatus').value;
    const nameSearch = document.getElementById('searchName').value.toLowerCase();
    const projectSearch = document.getElementById('searchProject').value.toLowerCase();

    filteredData = userData.filter(user => {
        const matchesStatus = !statusFilter || user.resultado === statusFilter;
        const matchesName = !nameSearch || user.nome.toLowerCase().includes(nameSearch);
        const matchesProject = !projectSearch || user.projeto.toLowerCase().includes(projectSearch);
        return matchesStatus && matchesName && matchesProject;
    });

    const sortBy = document.getElementById('sortBy').value;
    filteredData.sort((a, b) => {
        switch(sortBy) {
            case 'nome': return a.nome.localeCompare(b.nome);
            case 'nome-desc': return b.nome.localeCompare(a.nome);
            case 'pontuacao': return b.pontuacao - a.pontuacao;
            case 'pontuacao-desc': return a.pontuacao - b.pontuacao;
            case 'created_at': return new Date(b.created_at) - new Date(a.created_at);
            case 'created_at-desc': return new Date(a.created_at) - new Date(b.created_at);
            default: return 0;
        }
    });

    renderTable();
}

// Exporta os dados filtrados para Excel
function exportToExcel() {
    const wb = XLSX.utils.book_new();
    const header = ['Nome', 'Projeto', 'Resultado', 'Pontua√ß√£o', 'WhatsApp', 'E-mail', 'Data de preenchimento', 'Plano de neg√≥cios', 'TamSamSom', 'Pesquisa qualitativa', 'Mapeamento concorrentes', 'Persona', 'Pitch', 'Programas'];
    const rows = filteredData.map(user => {
        return [user.nome, user.projeto, user.resultado, user.pontuacao, user.whatsapp, user.email, user.created_at,
            user.respostas_originais.plano_negocios, user.respostas_originais.tam_sam_som, user.respostas_originais.pesquisa_qualitativa, 
            user.respostas_originais.mapeamento_concorrentes, user.respostas_originais.persona, user.respostas_originais.pitch, user.respostas_originais.programas];
    });
    const ws = XLSX.utils.aoa_to_sheet([header, ...rows]);
    XLSX.utils.book_append_sheet(wb, ws, 'Incubacao');
    XLSX.writeFile(wb, 'incubacao_' + new Date().toISOString().split('T')[0] + '.xlsx');
}

